import React, { useState, useEffect } from 'react';
import { X, User, Search, Plus, Phone } from 'lucide-react';
import axios from 'axios';

const NuovoOrdineCliente = ({ ordine, onUpdate, onClose, onNext }) => {
  // Stato per memorizzare i clienti trovati nella ricerca
  const [clientiTrovati, setClientiTrovati] = useState([]);
  
  // Stato per la ricerca
  const [searchQuery, setSearchQuery] = useState('');
  
  // Stato per il caricamento e gli errori
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  
  // Stato per il form di creazione nuovo cliente
  const [showNuovoCliente, setShowNuovoCliente] = useState(false);
  const [nuovoCliente, setNuovoCliente] = useState({
    nome: '',
    telefono: '',
    indirizzo: ''
  });
  
  // Stati per gli errori di validazione
  const [formErrors, setFormErrors] = useState({});
  
  // Effetto per cercare i clienti quando cambia la query di ricerca
  useEffect(() => {
    if (searchQuery.length >= 2) {
      cercaClienti();
    } else {
      setClientiTrovati([]);
    }
  }, [searchQuery]);
  
  // Funzione per cercare i clienti nel database
  const cercaClienti = async () => {
    if (searchQuery.length < 2) return;
    
    setIsLoading(true);
    setError(null);
    
    try {
      // Normalmente faremo una chiamata all'API per cercare i clienti
      // Ma per ora simuliamo con alcuni dati di esempio
      // const response = await axios.get(`/api/clienti/search?q=${searchQuery}`);
      
      // Simulazione di risposta dal server
      const response = {
        data: {
          success: true,
          data: [
            { 
              _id: '1', 
              nome: 'Mario Rossi', 
              telefono: '3331234567', 
              indirizzo: 'Via Roma 123, Milano',
              badge: 'vip'
            },
            { 
              _id: '2', 
              nome: 'Giulia Bianchi', 
              telefono: '3339876543', 
              indirizzo: 'Via Verdi 45, Milano',
              badge: 'oro'
            },
            { 
              _id: '3', 
              nome: 'Marco Verdi', 
              telefono: '3351122334', 
              indirizzo: 'Via Garibaldi 67, Milano',
              badge: 'argento'
            }
          ].filter(cliente => 
            cliente.nome.toLowerCase().includes(searchQuery.toLowerCase()) ||
            cliente.telefono.includes(searchQuery)
          )
        }
      };
      
      setTimeout(() => {
        setClientiTrovati(response.data.data);
        setIsLoading(false);
      }, 500);
      
    } catch (err) {
      setError('Errore nella ricerca dei clienti');
      console.error('Errore nella ricerca dei clienti:', err);
      setIsLoading(false);
    }
  };
  
  // Funzione per selezionare un cliente esistente
  const selezionaCliente = (cliente) => {
    onUpdate('clienteNome', cliente.nome);
    onUpdate('clienteTelefono', cliente.telefono);
    onUpdate('indirizzo', cliente.indirizzo || '');
    
    // Reset della ricerca
    setSearchQuery('');
    setClientiTrovati([]);
  };
  
  // Funzione per gestire gli input del nuovo cliente
  const handleInputChange = (campo, valore) => {
    setNuovoCliente({
      ...nuovoCliente,
      [campo]: valore
    });
    
    // Reset dell'errore quando l'utente modifica il campo
    if (formErrors[campo]) {
      setFormErrors({
        ...formErrors,
        [campo]: null
      });
    }
  };
  
  // Funzione per creare un nuovo cliente
  const creaNuovoCliente = () => {
    // Validazione dei campi
    const errors = {};
    
    if (!nuovoCliente.nome) {
      errors.nome = 'Il nome è obbligatorio';
    }
    
    if (!nuovoCliente.telefono) {
      errors.telefono = 'Il telefono è obbligatorio';
    } else if (!/^[0-9]{6,15}$/.test(nuovoCliente.telefono)) {
      errors.telefono = 'Inserisci un numero di telefono valido (solo numeri, 6-15 cifre)';
    }
    
    if (Object.keys(errors).length > 0) {
      setFormErrors(errors);
      return;
    }
    
    // In un'implementazione reale, qui salveremmo il cliente nel database
    // Per ora, lo passiamo direttamente all'ordine
    onUpdate('clienteNome', nuovoCliente.nome);
    onUpdate('clienteTelefono', nuovoCliente.telefono);
    onUpdate('indirizzo', nuovoCliente.indirizzo || '');
    
    // Reset del form e chiusura
    setNuovoCliente({
      nome: '',
      telefono: '',
      indirizzo: ''
    });
    setShowNuovoCliente(false);
  };
  
  // Funzione per passare allo step successivo
  const procediAvanti = () => {
    // Validazione dei campi obbligatori
    const errors = {};
    
    if (!ordine.clienteNome) {
      errors.clienteNome = 'Il nome del cliente è obbligatorio';
    }
    
    if (!ordine.clienteTelefono) {
      errors.clienteTelefono = 'Il telefono del cliente è obbligatorio';
    }
    
    if (Object.keys(errors).length > 0) {
      setFormErrors(errors);
      return;
    }
    
    // Passa allo step successivo
    onNext();
  };
  
  // Funzione di rendering del badge del cliente
  const renderBadgeCliente = (badge) => {
    if (!badge) return null;
    
    const badgeConfig = {
      vip: { bg: 'bg-purple-100', text: 'text-purple-700', label: 'VIP' },
      oro: { bg: 'bg-yellow-100', text: 'text-yellow-700', label: 'Oro' },
      argento: { bg: 'bg-gray-100', text: 'text-gray-700', label: 'Arg' },
      bronzo: { bg: 'bg-amber-100', text: 'text-amber-700', label: 'Bro' }
    };
    
    const config = badgeConfig[badge] || { bg: 'bg-gray-100', text: 'text-gray-700', label: badge };
    
    return (
      <span className={`${config.bg} ${config.text} px-2 py-1 rounded-full text-xs font-medium`}>
        {config.label}
      </span>
    );
  };
  
  return (
    <>
      <div className="flex items-center justify-between p-4 border-b">
        <div className="flex items-center">
          <button 
            className="mr-3 p-2 rounded-lg hover:bg-gray-100 transition-colors"
            onClick={onClose}
          >
            <X size={20} className="text-gray-700" />
          </button>
          <h2 className="text-xl font-semibold">Nuovo Ordine - Cliente</h2>
        </div>
      </div>
      
      <div className="p-4">
        {/* Header del wizard con i passi */}
        <div className="flex items-center justify-between mb-6 border-b pb-4">
          <div className="flex items-center w-1/4 relative">
            <div className="w-8 h-8 rounded-full bg-red-500 text-white flex items-center justify-center font-bold">
              <User size={18} />
            </div>
            <div className="ml-2">
              <span className="text-sm font-medium">Cliente</span>
            </div>
            <div className="absolute left-0 bottom-0 w-full h-1 bg-red-500"></div>
          </div>
          
          <div className="flex items-center w-1/4 relative opacity-50">
            <div className="w-8 h-8 rounded-full bg-gray-300 text-white flex items-center justify-center font-bold">
              2
            </div>
            <div className="ml-2">
              <span className="text-sm text-gray-500">Prodotti</span>
            </div>
          </div>
          
          <div className="flex items-center w-1/4 relative opacity-50">
            <div className="w-8 h-8 rounded-full bg-gray-300 text-white flex items-center justify-center font-bold">
              3
            </div>
            <div className="ml-2">
              <span className="text-sm text-gray-500">Dettagli</span>
            </div>
          </div>
          
          <div className="flex items-center w-1/4 relative opacity-50">
            <div className="w-8 h-8 rounded-full bg-gray-300 text-white flex items-center justify-center font-bold">
              4
            </div>
            <div className="ml-2">
              <span className="text-sm text-gray-500">Conferma</span>
            </div>
          </div>
        </div>
        
        {/* Ricerca cliente */}
        <div className="mb-6">
          <h3 className="font-medium text-gray-700 mb-3">Seleziona Cliente</h3>
          
          <div className="relative">
            <input 
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="Cerca per nome o telefono..."
              className="w-full border border-gray-300 rounded-lg pl-10 py-2 pr-3 focus:outline-none focus:ring-2 focus:ring-red-500"
            />
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={18} />
          </div>
          
          {/* Risultati ricerca */}
          {isLoading ? (
            <div className="mt-4 text-center">
              <div className="animate-spin rounded-full h-6 w-6 border-t-2 border-b-2 border-red-500 mx-auto"></div>
              <p className="text-gray-500 mt-2">Ricerca in corso...</p>
            </div>
          ) : clientiTrovati.length > 0 ? (
            <div className="mt-4 border rounded-lg divide-y overflow-hidden">
              {clientiTrovati.map(cliente => (
                <div 
                  key={cliente._id}
                  className="p-3 hover:bg-gray-50 cursor-pointer transition-colors"
                  onClick={() => selezionaCliente(cliente)}
                >
                  <div className="flex justify-between items-center">
                    <div>
                      <div className="font-medium">{cliente.nome}</div>
                      <div className="text-sm text-gray-500 flex items-center">
                        <Phone size={14} className="mr-1" />
                        {cliente.telefono}
                      </div>
                    </div>
                    
                    {renderBadgeCliente(cliente.badge)}
                  </div>
                </div>
              ))}
            </div>
          ) : searchQuery.length > 0 ? (
            <div className="mt-4 text-center py-6 border rounded-lg">
              <p className="text-gray-500">Nessun cliente trovato</p>
            </div>
          ) : null}
          
          {/* Pulsante per nuovo cliente */}
          <button 
            className="mt-4 flex items-center text-blue-500 hover:text-blue-700"
            onClick={() => setShowNuovoCliente(true)}
          >
            <Plus size={18} className="mr-1" />
            <span>Nuovo Cliente</span>
          </button>
        </div>
        
        {/* Form nuovo cliente */}
        {showNuovoCliente && (
          <div className="mt-6 border rounded-lg p-4 bg-gray-50">
            <h3 className="font-medium text-gray-700 mb-3">Nuovo Cliente</h3>
            
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
              <div>
                <label className="block text-sm text-gray-600 mb-1">Nome*</label>
                <input 
                  type="text"
                  value={nuovoCliente.nome}
                  onChange={(e) => handleInputChange('nome', e.target.value)}
                  className={`w-full border ${formErrors.nome ? 'border-red-500' : 'border-gray-300'} rounded-lg p-2`}
                  placeholder="Nome e Cognome"
                />
                {formErrors.nome && (
                  <p className="text-red-500 text-xs mt-1">{formErrors.nome}</p>
                )}
              </div>
              
              <div>
                <label className="block text-sm text-gray-600 mb-1">Telefono*</label>
                <input 
                  type="tel"
                  value={nuovoCliente.telefono}
                  onChange={(e) => handleInputChange('telefono', e.target.value)}
                  className={`w-full border ${formErrors.telefono ? 'border-red-500' : 'border-gray-300'} rounded-lg p-2`}
                  placeholder="Numero di telefono"
                />
                {formErrors.telefono && (
                  <p className="text-red-500 text-xs mt-1">{formErrors.telefono}</p>
                )}
              </div>
            </div>
            
            <div className="mb-4">
              <label className="block text-sm text-gray-600 mb-1">Indirizzo</label>
              <input 
                type="text"
                value={nuovoCliente.indirizzo}
                onChange={(e) => handleInputChange('indirizzo', e.target.value)}
                className="w-full border border-gray-300 rounded-lg p-2"
                placeholder="Via, numero civico, città"
              />
            </div>
            
            <div className="flex justify-end space-x-2">
              <button 
                className="px-4 py-2 text-gray-600 hover:text-gray-800"
                onClick={() => setShowNuovoCliente(false)}
              >
                Annulla
              </button>
              
              <button 
                className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition-colors"
                onClick={creaNuovoCliente}
              >
                Salva Cliente
              </button>
            </div>
          </div>
        )}
        
        {/* Cliente selezionato */}
        {ordine.clienteNome && (
          <div className="mt-6 border rounded-lg p-4">
            <h3 className="font-medium text-gray-700 mb-3">Cliente Selezionato</h3>
            
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm text-gray-600 mb-1">Nome</label>
                <input 
                  type="text"
                  value={ordine.clienteNome}
                  onChange={(e) => onUpdate('clienteNome', e.target.value)}
                  className={`w-full border ${formErrors.clienteNome ? 'border-red-500' : 'border-gray-300'} rounded-lg p-2`}
                />
                {formErrors.clienteNome && (
                  <p className="text-red-500 text-xs mt-1">{formErrors.clienteNome}</p>
                )}
              </div>
              
              <div>
                <label className="block text-sm text-gray-600 mb-1">Telefono</label>
                <input 
                  type="tel"
                  value={ordine.clienteTelefono}
                  onChange={(e) => onUpdate('clienteTelefono', e.target.value)}
                  className={`w-full border ${formErrors.clienteTelefono ? 'border-red-500' : 'border-gray-300'} rounded-lg p-2`}
                />
                {formErrors.clienteTelefono && (
                  <p className="text-red-500 text-xs mt-1">{formErrors.clienteTelefono}</p>
                )}
              </div>
            </div>
            
            <div className="mt-3">
              <label className="block text-sm text-gray-600 mb-1">Indirizzo</label>
              <input 
                type="text"
                value={ordine.indirizzo}
                onChange={(e) => onUpdate('indirizzo', e.target.value)}
                className="w-full border border-gray-300 rounded-lg p-2"
              />
            </div>
          </div>
        )}
        
        {/* Pulsanti azioni */}
        <div className="mt-6 flex justify-between">
          <button 
            className="px-4 py-2 border border-gray-300 rounded-lg text-gray-600 hover:bg-gray-100 transition-colors"
            onClick={onClose}
          >
            Annulla
          </button>
          
          <button 
            className="px-6 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition-colors"
            onClick={procediAvanti}
          >
            Continua
          </button>
        </div>
      </div>
    </>
  );
};

export default NuovoOrdineCliente;
