import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  ShoppingCart, Search, Filter, PlusCircle, ArrowLeft, RefreshCw,
  User, Home, Package, Phone, Calendar, Clock, DollarSign, FileText,
  ChevronDown, ChevronRight, ChevronLeft, Edit, Trash2, Eye, CheckCircle,
  AlertTriangle, List, Grid, Clipboard, Download, MoreHorizontal, CreditCard, 
  Truck, Tag
} from 'lucide-react';
import { getOrdini, getOrdine, deleteOrdine } from '../../services/ordiniService';
import NuovoOrdineForm from './NuovoOrdineForm';
import OrdineDettaglio from './OrdineDettaglio';

const OrdiniPage = () => {
  // Stati per la gestione dati
  const [ordini, setOrdini] = useState([]);
  const [ordineSelezionato, setOrdineSelezionato] = useState(null);
  const [filtri, setFiltri] = useState({
    stato: '',
    tipoOrdine: '',
    data: new Date().toISOString().split('T')[0], // Data odierna come default
    cliente: ''
  });
  const [paginaCorrente, setPaginaCorrente] = useState(1);
  const [itemPerPagina, setItemPerPagina] = useState(10);
  const [caricamento, setCaricamento] = useState(false);
  const [filtroPannello, setFiltroPannello] = useState(false);
  const [visualizzazioneGriglia, setVisualizzazioneGriglia] = useState(false);
  const [modalNuovoOrdine, setModalNuovoOrdine] = useState(false);
  const [modalDettaglio, setModalDettaglio] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');

  const navigate = useNavigate();

  // Funzione per caricare gli ordini
  const fetchOrdini = useCallback(async () => {
    try {
      setCaricamento(true);
      const data = await getOrdini(filtri);
      setOrdini(data);
   } catch (error) {
      console.error('Errore nel caricamento degli ordini:', error);
      alert('Errore nel recupero degli ordini. Riprova più tardi.');
    } finally {
      setCaricamento(false);
    }
  }, [filtri]);

  // Caricamento iniziale degli ordini
  useEffect(() => {
    fetchOrdini();
  }, [fetchOrdini]);

  // Filtraggio degli ordini in base alla ricerca
  const ordiniFiltrati = ordini.filter(ordine => {
    if (!searchQuery) return true;
    
    const query = searchQuery.toLowerCase();
    return (
      ordine.numeroOrdine.toLowerCase().includes(query) ||
      (ordine.cliente?.nome || '').toLowerCase().includes(query) ||
      (ordine.datiCliente?.nome || '').toLowerCase().includes(query) ||
      (ordine.datiCliente?.telefono || '').includes(query)
    );
  });

  // Paginazione
  const indicePrimoItem = (paginaCorrente - 1) * itemPerPagina;
  const indiceUltimoItem = Math.min(indicePrimoItem + itemPerPagina, ordiniFiltrati.length);
  const ordiniPaginati = ordiniFiltrati.slice(indicePrimoItem, indiceUltimoItem);
  const totalePagine = Math.ceil(ordiniFiltrati.length / itemPerPagina);

  // Funzione per cambiare il filtro
  const handleChangeFiltro = (e) => {
    const { name, value } = e.target;
    setFiltri(prev => ({ ...prev, [name]: value }));
    setPaginaCorrente(1); // Reset alla prima pagina quando cambia un filtro
  };

  // Funzione per aprire il pannello filtri
  const toggleFiltroPannello = () => {
    setFiltroPannello(!filtroPannello);
  };

  // Funzione per cambiare la visualizzazione (griglia/lista)
  const toggleVisualizzazione = () => {
    setVisualizzazioneGriglia(!visualizzazioneGriglia);
  };

  // Funzione per aprire il form di nuovo ordine
  const handleNuovoOrdine = () => {
    setModalNuovoOrdine(true);
  };

  // Funzione per chiudere il form di nuovo ordine
  const handleChiudiNuovoOrdine = () => {
    setModalNuovoOrdine(false);
    // Ricarica gli ordini dopo aver creato un nuovo ordine
    fetchOrdini();
  };

  // Funzione per visualizzare i dettagli di un ordine
  const handleVisualizzaOrdine = async (id) => {
    try {
      setCaricamento(true);
      const data = await getOrdine(id);
      setOrdineSelezionato(data);
      setModalDettaglio(true);
    } catch (error) {
      console.error('Errore nel recupero dei dettagli ordine:', error);
      alert('Errore nel recupero dei dettagli. Riprova più tardi.');
    } finally {
      setCaricamento(false);
    }
  };

  // Funzione per chiudere il pannello dettagli
  const handleChiudiDettaglio = () => {
    setModalDettaglio(false);
    setOrdineSelezionato(null);
  };

  // Funzione per eliminare un ordine
  const handleEliminaOrdine = async (id) => {
    if (window.confirm('Sei sicuro di voler eliminare questo ordine?')) {
      try {
        setCaricamento(true);
        await deleteOrdine(id);
        // Rimuovi l'ordine dalla lista locale
        setOrdini(ordini.filter(ordine => ordine._id !== id));
        // Chiudi il pannello dettagli se aperto
        if (ordineSelezionato && ordineSelezionato._id === id) {
          setModalDettaglio(false);
          setOrdineSelezionato(null);
        }
        alert('Ordine eliminato con successo');
      } catch (error) {
        console.error('Errore nell\'eliminazione dell\'ordine:', error);
        alert('Errore nell\'eliminazione dell\'ordine. Riprova più tardi.');
      } finally {
        setCaricamento(false);
      }
    }
  };

  // Funzione per formattare la data
  const formatDate = (dateString) => {
    if (!dateString) return '';
    
    const options = { 
      year: 'numeric', 
      month: '2-digit', 
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    };
    
    return new Date(dateString).toLocaleString('it-IT', options);
  };

  // Funzione per il colore del badge stato
  const getStatoColor = (stato) => {
    switch (stato) {
      case 'Nuovo':
        return 'bg-blue-100 text-blue-800';
      case 'In preparazione':
        return 'bg-amber-100 text-amber-800';
      case 'Pronto':
        return 'bg-green-100 text-green-800';
      case 'Consegnato':
        return 'bg-teal-100 text-teal-800';
      case 'Annullato':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  // Funzione per il colore del badge tipo ordine
  const getTipoOrdineColor = (tipo) => {
    switch (tipo) {
      case 'Asporto':
        return 'bg-orange-100 text-orange-800';
      case 'Tavolo':
        return 'bg-indigo-100 text-indigo-800';
      case 'Delivery':
        return 'bg-purple-100 text-purple-800';
      case 'Online':
        return 'bg-cyan-100 text-cyan-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  // Icona per il tipo di ordine
  const getTipoOrdineIcon = (tipo) => {
    switch (tipo) {
      case 'Asporto':
        return <Package size={16} />;
      case 'Tavolo':
        return <User size={16} />;
      case 'Delivery':
        return <Truck size={16} />;
      case 'Online':
        return <CreditCard size={16} />;
      default:
        return <ShoppingCart size={16} />;
    }
  };

  // Esporta ordini in CSV
  const exportCSV = () => {
    // Intestazioni CSV
    const headers = [
      'Numero Ordine', 
      'Data Ordine', 
      'Orario Consegna', 
      'Cliente', 
      'Tipo Ordine',
      'Stato',
      'Totale',
      'Metodo Pagamento'
    ];
    
    // Dati CSV
    const rows = ordini.map(ordine => [
      ordine.numeroOrdine,
      formatDate(ordine.dataOrdine).split(',')[0], // Solo la data
      formatDate(ordine.orarioConsegna),
      ordine.cliente?.nome || ordine.datiCliente?.nome || 'N/D',
      ordine.tipoOrdine,
      ordine.stato,
      `€ ${ordine.totale.toFixed(2)}`,
      ordine.metodoPagamento
    ]);
    
    // Concatena intestazioni e righe
    const csvContent = [
      headers.join(','),
      ...rows.map(row => row.join(','))
    ].join('\n');
    
    // Crea un file CSV e scaricalo
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    
    link.setAttribute('href', url);
    link.setAttribute('download', `ordini_${new Date().toISOString().split('T')[0]}.csv`);
    link.style.visibility = 'hidden';
    
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div className="bg-gradient-to-br from-orange-50 to-orange-100 min-h-screen p-6">
      {/* Header */}
      <header className="flex items-center justify-between mb-6">
        <div className="flex items-center">
          <button 
            onClick={() => navigate('/')} 
            className="mr-4 p-2 rounded-full hover:bg-white hover:shadow-md transition-all"
          >
            <ArrowLeft className="text-gray-600" />
          </button>
          <div className="flex items-center">
            <ShoppingCart className="text-red-500 mr-2" size={28} />
            <h1 className="text-2xl font-bold text-gray-800">Gestione Ordini</h1>
          </div>
        </div>
        <div className="flex items-center space-x-3">
          <button 
            className="bg-white rounded-lg px-3 py-2 shadow-md flex items-center space-x-2 hover:bg-gray-100 transition-colors"
            onClick={exportCSV}
          >
            <Download className="text-gray-600" size={18} />
            <span className="text-gray-700 text-sm">Esporta</span>
          </button>
          <button 
            className="bg-red-500 rounded-lg px-3 py-2 shadow-md flex items-center space-x-2 hover:bg-red-600 transition-colors text-white"
            onClick={handleNuovoOrdine}
          >
            <PlusCircle size={18} />
            <span className="text-sm">Nuovo Ordine</span>
          </button>
        </div>
      </header>

      {/* Barra di ricerca e filtri */}
      <div className="bg-white rounded-xl shadow-md p-4 mb-6">
        <div className="flex flex-wrap items-center justify-between gap-4">
          <div className="relative w-full md:w-auto flex-grow md:max-w-md">
            <input
              type="text"
              placeholder="Cerca ordine, cliente..."
              className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg w-full focus:outline-none focus:ring-2 focus:ring-red-500"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <Search className="absolute left-3 top-2.5 text-gray-400" size={18} />
          </div>
          
          <div className="flex items-center space-x-3 flex-wrap">
            <div className="relative">
              <select
                name="stato"
                value={filtri.stato}
                onChange={handleChangeFiltro}
                className="pl-3 pr-8 py-2 border border-gray-300 rounded-lg appearance-none focus:outline-none focus:ring-2 focus:ring-red-500"
              >
                <option value="">Tutti gli stati</option>
                <option value="Nuovo">Nuovo</option>
                <option value="In preparazione">In preparazione</option>
                <option value="Pronto">Pronto</option>
                <option value="Consegnato">Consegnato</option>
                <option value="Annullato">Annullato</option>
              </select>
              <ChevronDown className="absolute right-2 top-2.5 text-gray-500 pointer-events-none" size={16} />
            </div>
            
            <div className="relative">
              <select
                name="tipoOrdine"
                value={filtri.tipoOrdine}
                onChange={handleChangeFiltro}
                className="pl-3 pr-8 py-2 border border-gray-300 rounded-lg appearance-none focus:outline-none focus:ring-2 focus:ring-red-500"
              >
                <option value="">Tutti i tipi</option>
                <option value="Asporto">Asporto</option>
                <option value="Tavolo">Tavolo</option>
                <option value="Delivery">Delivery</option>
                <option value="Online">Online</option>
              </select>
              <ChevronDown className="absolute right-2 top-2.5 text-gray-500 pointer-events-none" size={16} />
            </div>
            
            <input
              type="date"
              name="data"
              value={filtri.data}
              onChange={handleChangeFiltro}
              className="px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-red-500"
            />
            
            <button 
              className="p-2 rounded-lg border border-gray-300 hover:bg-gray-100 flex items-center"
              onClick={toggleFiltroPannello}
            >
              <Filter size={18} className="text-gray-600" />
            </button>
            
            <button 
              className="p-2 rounded-lg border border-gray-300 hover:bg-gray-100 flex items-center"
              onClick={toggleVisualizzazione}
            >
              {visualizzazioneGriglia ? 
                <List size={18} className="text-gray-600" /> : 
                <Grid size={18} className="text-gray-600" />
              }
            </button>
            
            <button 
              className={`p-2 rounded-lg border border-gray-300 hover:bg-gray-100 ${caricamento ? 'opacity-50' : ''}`}
              onClick={fetchOrdini}
              disabled={caricamento}
            >
              <RefreshCw size={18} className={`text-gray-600 ${caricamento ? 'animate-spin' : ''}`} />
            </button>
          </div>
        </div>
        
        {/* Pannello filtri avanzati */}
        {filtroPannello && (
          <div className="mt-4 p-3 border-t border-gray-200">
            <h3 className="font-medium text-gray-700 mb-3">Filtri avanzati</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              <div>
                <label className="block text-sm text-gray-600 mb-1">Cliente</label>
                <input
                  type="text"
                  name="cliente"
                  value={filtri.cliente}
                  onChange={handleChangeFiltro}
                  placeholder="Nome cliente..."
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-red-500"
                />
              </div>
              
              {/* Altri filtri avanzati potrebbero essere aggiunti qui */}
            </div>
          </div>
        )}
      </div>

      {/* Lista ordini (vista griglia o tabella) */}
      {visualizzazioneGriglia ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {caricamento ? (
            <div className="col-span-full flex justify-center items-center py-12">
              <RefreshCw className="animate-spin text-red-500 mr-2" size={24} />
              <span className="text-gray-500 text-lg">Caricamento ordini...</span>
            </div>
          ) : ordiniPaginati.length === 0 ? (
            <div className="col-span-full bg-white rounded-xl p-8 shadow-md text-center">
              <AlertTriangle className="text-amber-500 mx-auto mb-3" size={32} />
              <h3 className="text-lg font-bold text-gray-700 mb-1">Nessun ordine trovato</h3>
              <p className="text-gray-500">Prova a modificare i filtri o a creare un nuovo ordine</p>
              <button 
                onClick={handleNuovoOrdine}
                className="mt-4 px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition-colors"
              >
                <PlusCircle className="inline mr-2" size={16} />
                Nuovo Ordine
              </button>
            </div>
          ) : (
            ordiniPaginati.map(ordine => (
              <div 
                key={ordine._id} 
                className="bg-white rounded-xl shadow-md p-4 hover:shadow-lg transition-shadow"
              >
                <div className="flex justify-between items-start mb-2">
                  <h3 className="font-bold text-gray-800">{ordine.numeroOrdine}</h3>
                  <span className={`text-xs px-2 py-1 rounded-full ${getStatoColor(ordine.stato)}`}>
                    {ordine.stato}
                  </span>
                </div>
                
                <div className="flex items-center text-sm text-gray-600 mb-1">
                  <Calendar className="mr-1" size={14} />
                  <span>{formatDate(ordine.dataOrdine)}</span>
                </div>
                
                <div className="flex items-center text-sm text-gray-600 mb-3">
                  <Clock className="mr-1" size={14} />
                  <span>Consegna: {formatDate(ordine.orarioConsegna)}</span>
                </div>
                
                <div className="flex justify-between items-center mb-3">
                  <div className="flex items-center">
                    <User className="text-gray-500 mr-1" size={16} />
                    <span className="text-gray-700">
                      {ordine.cliente?.nome || ordine.datiCliente?.nome || 'Cliente non specificato'}
                    </span>
                  </div>
                  <span className={`text-xs px-2 py-1 rounded-full flex items-center ${getTipoOrdineColor(ordine.tipoOrdine)}`}>
                    {getTipoOrdineIcon(ordine.tipoOrdine)}
                    <span className="ml-1">{ordine.tipoOrdine}</span>
                  </span>
                </div>
                
                <div className="bg-gray-50 p-2 rounded mb-3">
                  <div className="font-medium text-gray-700 mb-1">Prodotti:</div>
                  <ul className="text-sm">
                    {ordine.prodotti?.slice(0, 2).map((p, index) => (
                      <li key={index} className="flex justify-between">
                        <span>{p.quantita}x {p.nome}</span>
                        <span className="text-gray-600">€ {(p.inPromozione ? p.prezzoPromo : p.prezzo).toFixed(2)}</span>
                      </li>
                    ))}
                    {(ordine.prodotti?.length || 0) > 2 && (
                      <li className="text-gray-500 text-center mt-1">+ altri {ordine.prodotti.length - 2} prodotti</li>
                    )}
                  </ul>
                </div>
                
                <div className="flex justify-between items-center mb-3">
                  <span className="font-medium text-gray-700">Totale:</span>
                  <span className="font-bold text-red-600">€ {ordine.totale.toFixed(2)}</span>
                </div>
                
                <div className="flex justify-end space-x-2">
                  <button 
                    className="p-1.5 rounded-lg bg-blue-50 text-blue-600 hover:bg-blue-100"
                    onClick={() => handleVisualizzaOrdine(ordine._id)}
                  >
                    <Eye size={16} />
                  </button>
                  <button 
                    className="p-1.5 rounded-lg bg-amber-50 text-amber-600 hover:bg-amber-100"
                    onClick={() => handleVisualizzaOrdine(ordine._id)}
                  >
                    <Edit size={16} />
                  </button>
                  <button 
                    className="p-1.5 rounded-lg bg-red-50 text-red-600 hover:bg-red-100"
                    onClick={() => handleEliminaOrdine(ordine._id)}
                  >
                    <Trash2 size={16} />
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      ) : (
        <div className="bg-white rounded-xl shadow-md overflow-hidden">
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="bg-gray-50 border-b border-gray-200">
                  <th className="px-4 py-3 text-left text-sm font-medium text-gray-600">Numero</th>
                  <th className="px-4 py-3 text-left text-sm font-medium text-gray-600">Data</th>
                  <th className="px-4 py-3 text-left text-sm font-medium text-gray-600">Orario</th>
                  <th className="px-4 py-3 text-left text-sm font-medium text-gray-600">Cliente</th>
                  <th className="px-4 py-3 text-left text-sm font-medium text-gray-600">Tipo</th>
                  <th className="px-4 py-3 text-left text-sm font-medium text-gray-600">Stato</th>
                  <th className="px-4 py-3 text-right text-sm font-medium text-gray-600">Totale</th>
                  <th className="px-4 py-3 text-center text-sm font-medium text-gray-600">Azioni</th>
                </tr>
              </thead>
              <tbody>
                {caricamento ? (
                  <tr>
                    <td colSpan={8} className="text-center py-8">
                      <RefreshCw className="animate-spin text-red-500 mx-auto mb-2" size={24} />
                      <span className="text-gray-500">Caricamento ordini...</span>
                    </td>
                  </tr>
                ) : ordiniPaginati.length === 0 ? (
                  <tr>
                    <td colSpan={8} className="text-center py-8">
                      <AlertTriangle className="text-amber-500 mx-auto mb-2" size={24} />
                      <p className="text-gray-500 mb-3">Nessun ordine trovato con i filtri selezionati</p>
                      <button 
                        onClick={handleNuovoOrdine}
                        className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition-colors"
                      >
                        <PlusCircle className="inline mr-2" size={16} />
                        Nuovo Ordine
                      </button>
                    </td>
                  </tr>
                ) : (
                  ordiniPaginati.map(ordine => (
                    <tr 
                      key={ordine._id} 
                      className="border-b border-gray-100 hover:bg-gray-50 cursor-pointer"
                      onClick={() => handleVisualizzaOrdine(ordine._id)}
                    >
                      <td className="px-4 py-3">
                        <span className="font-medium text-gray-800">{ordine.numeroOrdine}</span>
                      </td>
                      <td className="px-4 py-3 text-sm text-gray-600">
                        {formatDate(ordine.dataOrdine).split(',')[0]}
                      </td>
                      <td className="px-4 py-3 text-sm text-gray-600">
                        {formatDate(ordine.orarioConsegna).split(',')[1]}
                      </td>
                      <td className="px-4 py-3">
                        <div className="flex items-center">
                          <span className="text-gray-800">
                            {ordine.cliente?.nome || ordine.datiCliente?.nome || 'N/D'}
                          </span>
                        </div>
                      </td>
                      <td className="px-4 py-3">
                        <span className={`text-xs px-2 py-1 rounded-full flex items-center w-fit ${getTipoOrdineColor(ordine.tipoOrdine)}`}>
                          {getTipoOrdineIcon(ordine.tipoOrdine)}
                          <span className="ml-1">{ordine.tipoOrdine}</span>
                        </span>
                      </td>
                      <td className="px-4 py-3">
                        <span className={`text-xs px-2 py-1 rounded-full ${getStatoColor(ordine.stato)}`}>
                          {ordine.stato}
                        </span>
                      </td>
                      <td className="px-4 py-3 text-right">
                        <span className="font-bold text-red-600">€ {ordine.totale.toFixed(2)}</span>
                      </td>
                      <td className="px-4 py-3">
                        <div className="flex justify-center space-x-2">
                          <button 
                            className="p-1.5 rounded-lg bg-blue-50 text-blue-600 hover:bg-blue-100"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleVisualizzaOrdine(ordine._id);
                            }}
                          >
                            <Eye size={16} />
                          </button>
                          <button 
                            className="p-1.5 rounded-lg bg-amber-50 text-amber-600 hover:bg-amber-100"
                            onClick={(e) => {
                              e.stopPropagation();
                              // Implementare modifica
                              handleVisualizzaOrdine(ordine._id);
                            }}
                          >
                            <Edit size={16} />
                          </button>
                          <button 
                            className="p-1.5 rounded-lg bg-red-50 text-red-600 hover:bg-red-100"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleEliminaOrdine(ordine._id);
                            }}
                          >
                            <Trash2 size={16} />
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
          
          {/* Paginazione */}
          {ordiniFiltrati.length > 0 && (
            <div className="flex justify-between items-center p-4 border-t border-gray-200">
              <div className="text-sm text-gray-600">
                Mostrando {indicePrimoItem + 1} - {indiceUltimoItem} di {ordiniFiltrati.length} ordini
              </div>
              <div className="flex items-center space-x-1">
                <button 
                  className="p-2 rounded-lg border border-gray-300 hover:bg-gray-100 disabled:opacity-50"
                  disabled={paginaCorrente === 1}
                  onClick={() => setPaginaCorrente(prev => Math.max(prev - 1, 1))}
                >
                  <ChevronLeft size={16} className="text-gray-600" />
                </button>
                
                {/* Pulsanti pagina */}
                {[...Array(totalePagine)].map((_, index) => (
                  <button 
                    key={index}
                    className={`w-8 h-8 rounded-lg border ${paginaCorrente === index + 1 ? 'bg-red-500 text-white border-red-500' : 'border-gray-300 hover:bg-gray-100'}`}
                    onClick={() => setPaginaCorrente(index + 1)}
                  >
                    {index + 1}
                  </button>
                )).slice(Math.max(0, paginaCorrente - 3), Math.min(totalePagine, paginaCorrente + 2))}
                
                <button 
                  className="p-2 rounded-lg border border-gray-300 hover:bg-gray-100 disabled:opacity-50"
                  disabled={paginaCorrente === totalePagine}
                  onClick={() => setPaginaCorrente(prev => Math.min(prev + 1, totalePagine))}
                >
                  <ChevronRight size={16} className="text-gray-600" />
                </button>
              </div>
            </div>
          )}
        </div>
      )}

      {/* Modal per nuovo ordine */}
      {modalNuovoOrdine && (
        <NuovoOrdineForm onClose={handleChiudiNuovoOrdine} />
      )}

      {/* Modal per dettaglio ordine */}
      {modalDettaglio && ordineSelezionato && (
        <OrdineDettaglio 
          ordine={ordineSelezionato} 
          onClose={handleChiudiDettaglio} 
          onDelete={handleEliminaOrdine}
          onRefresh={fetchOrdini}
        />
      )}
    </div>
  );
};

export default OrdiniPage;
