import React, { useState, useEffect } from 'react';
import { Modal, Form, ListGroup, Badge, InputGroup } from 'react-bootstrap';
import { FaSearch } from 'react-icons/fa';

const ProductSelect = ({ show, prodotti, onClose, onSelectProdotto }) => {
  const [filteredProdotti, setFilteredProdotti] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [categoriaFilter, setCategoriaFilter] = useState('');

  // Filtra i prodotti in base alla ricerca e alla categoria
  useEffect(() => {
    if (!prodotti) return;
    
    let filtered = [...prodotti];
    
    // Filtra per categoria
    if (categoriaFilter) {
      filtered = filtered.filter(p => p.categoria === categoriaFilter);
    }
    
    // Filtra per termine di ricerca
    if (searchTerm) {
      const term = searchTerm.toLowerCase();
      filtered = filtered.filter(p => 
        p.nome.toLowerCase().includes(term) || 
        (p.descrizione && p.descrizione.toLowerCase().includes(term))
      );
    }
    
    // Ordina per categoria e poi per ordinePriorita
    filtered.sort((a, b) => {
      if (a.categoria !== b.categoria) {
        return a.categoria.localeCompare(b.categoria);
      }
      return (a.ordinePriorita || 100) - (b.ordinePriorita || 100);
    });
    
    setFilteredProdotti(filtered);
  }, [prodotti, searchTerm, categoriaFilter]);

  // Gestisce il cambiamento nel campo di ricerca
  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  // Gestisce il cambiamento nel filtro per categoria
  const handleCategoriaChange = (e) => {
    setCategoriaFilter(e.target.value);
  };

  // Formatta prezzo in EUR
  const formatPrice = (price) => {
    return new Intl.NumberFormat('it-IT', { style: 'currency', currency: 'EUR' }).format(price);
  };

  // Mappa le categorie a nomi piÃ¹ amichevoli
  const categoriaNome = {
    'pizza': 'Pizze',
    'antipasto': 'Antipasti',
    'bevanda': 'Bevande',
    'dolce': 'Dolci',
    'contorno': 'Contorni',
    'altro': 'Altro'
  };

  // Reset del filtro quando si chiude il modal
  useEffect(() => {
    if (!show) {
      setSearchTerm('');
      setCategoriaFilter('');
    }
  }, [show]);

  return (
    <Modal show={show} onHide={onClose} size="lg">
      <Modal.Header closeButton>
        <Modal.Title>Seleziona Prodotto</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <div className="mb-3">
          <InputGroup>
            <InputGroup.Text>
              <FaSearch />
            </InputGroup.Text>
            <Form.Control
              type="text"
              placeholder="Cerca prodotto..."
              value={searchTerm}
              onChange={handleSearchChange}
            />
          </InputGroup>
        </div>
        
        <div className="mb-3">
          <Form.Select 
            value={categoriaFilter} 
            onChange={handleCategoriaChange}
          >
            <option value="">Tutte le categorie</option>
            <option value="pizza">Pizze</option>
            <option value="antipasto">Antipasti</option>
            <option value="bevanda">Bevande</option>
            <option value="dolce">Dolci</option>
            <option value="contorno">Contorni</option>
            <option value="altro">Altro</option>
          </Form.Select>
        </div>
        
        {filteredProdotti.length === 0 ? (
          <div className="text-center py-4 text-muted">
            Nessun prodotto trovato
          </div>
        ) : (
          <ListGroup style={{ maxHeight: '400px', overflowY: 'auto' }}>
            {filteredProdotti.map((prodotto) => (
              <ListGroup.Item 
                key={prodotto._id} 
                action 
                onClick={() => onSelectProdotto(prodotto)}
                className="d-flex justify-content-between align-items-center"
              >
                <div>
                  <div className="fw-bold">{prodotto.nome}</div>
                  {prodotto.descrizione && (
                    <div className="text-muted small">{prodotto.descrizione}</div>
                  )}
                  <div className="mt-1">
                    <Badge bg="light" text="dark" className="me-1">
                      {categoriaNome[prodotto.categoria]}
                    </Badge>
                    {prodotto.vegano && (
                      <Badge bg="success" className="me-1">Vegano</Badge>
                    )}
                    {prodotto.vegetariano && (
                      <Badge bg="info" className="me-1">Vegetariano</Badge>
                    )}
                    {prodotto.glutenFree && (
                      <Badge bg="warning" text="dark">Gluten Free</Badge>
                    )}
                  </div>
                </div>
                <div>
                  {prodotto.inPromozione && prodotto.prezzoPromozione ? (
                    <div>
                      <span className="text-decoration-line-through text-muted me-2">
                        {formatPrice(prodotto.prezzo)}
                      </span>
                      <span className="fw-bold text-danger">
                        {formatPrice(prodotto.prezzoPromozione)}
                      </span>
                    </div>
                  ) : (
                    <span className="fw-bold">
                      {formatPrice(prodotto.prezzo)}
                    </span>
                  )}
                </div>
              </ListGroup.Item>
            ))}
          </ListGroup>
        )}
      </Modal.Body>
    </Modal>
  );
};

export default ProductSelect;
