import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Alert } from 'react-bootstrap';
import { FaPlus, FaFilter } from 'react-icons/fa';
import OrdiniList from './OrdiniList';
import OrdiniFilter from './OrdiniFilter';
import OrdiniModal from './OrdiniModal';
import StatisticheOrdini from './StatisticheOrdini';
import { getOrdini } from '../../services/ordiniService';
import './OrdiniPage.css';

const OrdiniPage = () => {
  const [ordini, setOrdini] = useState([]);
  const [filtri, setFiltri] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [modalMode, setModalMode] = useState('create');
  const [ordineSelezionato, setOrdineSelezionato] = useState(null);
  const [showFilter, setShowFilter] = useState(false);
  const [viewMode, setViewMode] = useState('list'); // 'list' o 'stats'

  // Carica gli ordini all'avvio e quando cambiano i filtri
  useEffect(() => {
    fetchOrdini();
  }, [filtri]);

  // Funzione per recuperare gli ordini dal backend
  const fetchOrdini = async () => {
    setLoading(true);
    try {
      const result = await getOrdini(filtri);
      setOrdini(result.data);
      setError(null);
    } catch (err) {
      console.error('Errore nel caricamento degli ordini:', err);
      setError('Impossibile caricare gli ordini. Riprova piÃ¹ tardi.');
    } finally {
      setLoading(false);
    }
  };

  // Gestisce l'apertura del modal per creare un nuovo ordine
  const handleNuovoOrdine = () => {
    setModalMode('create');
    setOrdineSelezionato(null);
    setShowModal(true);
  };

  // Gestisce l'apertura del modal per visualizzare/modificare un ordine esistente
  const handleEditOrdine = (ordine) => {
    setModalMode('edit');
    setOrdineSelezionato(ordine);
    setShowModal(true);
  };

  // Gestisce la chiusura del modal
  const handleCloseModal = () => {
    setShowModal(false);
    setOrdineSelezionato(null);
  };

  // Gestisce il salvataggio di un ordine (nuovo o modificato)
  const handleSaveOrdine = () => {
    setShowModal(false);
    fetchOrdini(); // Ricarica gli ordini per mostrare l'aggiornamento
  };

  // Gestisce l'applicazione dei filtri
  const handleApplyFilter = (newFiltri) => {
    setFiltri(newFiltri);
    setShowFilter(false);
  };

  // Gestisce il reset dei filtri
  const handleResetFilter = () => {
    setFiltri({});
    setShowFilter(false);
  };

  return (
    <Container fluid className="ordini-page py-4">
      <Row className="mb-4">
        <Col>
          <h2 className="page-title">Gestione Ordini</h2>
        </Col>
        <Col xs="auto">
          <Button 
            variant="outline-primary" 
            className="me-2"
            onClick={() => setViewMode(viewMode === 'list' ? 'stats' : 'list')}
          >
            {viewMode === 'list' ? 'Visualizza Statistiche' : 'Visualizza Ordini'}
          </Button>
          <Button 
            variant="outline-secondary" 
            className="me-2"
            onClick={() => setShowFilter(!showFilter)}
          >
            <FaFilter /> Filtri
          </Button>
          <Button 
            variant="primary" 
            onClick={handleNuovoOrdine}
          >
            <FaPlus /> Nuovo Ordine
          </Button>
        </Col>
      </Row>

      {showFilter && (
        <Row className="mb-4">
          <Col>
            <Card>
              <Card.Body>
                <OrdiniFilter 
                  currentFiltri={filtri}
                  onApplyFilter={handleApplyFilter}
                  onResetFilter={handleResetFilter}
                />
              </Card.Body>
            </Card>
          </Col>
        </Row>
      )}

      {error && (
        <Row className="mb-4">
          <Col>
            <Alert variant="danger">{error}</Alert>
          </Col>
        </Row>
      )}

      <Row>
        <Col>
          {viewMode === 'list' ? (
            <OrdiniList 
              ordini={ordini}
              loading={loading}
              onEditOrdine={handleEditOrdine}
              onRefresh={fetchOrdini}
            />
          ) : (
            <StatisticheOrdini />
          )}
        </Col>
      </Row>

      <OrdiniModal 
        show={showModal}
        mode={modalMode}
        ordine={ordineSelezionato}
        onClose={handleCloseModal}
        onSave={handleSaveOrdine}
      />
    </Container>
  );
};

export default OrdiniPage;
