import React, { useState, useEffect } from 'react';
import { Row, Col, Form, Button } from 'react-bootstrap';
import DatePicker from 'react-datepicker';
import { it } from 'date-fns/locale';
import 'react-datepicker/dist/react-datepicker.css';
import { getClienti } from '../../services/clientiService';

const OrdiniFilter = ({ currentFiltri, onApplyFilter, onResetFilter }) => {
  const [filtri, setFiltri] = useState({
    stato: '',
    tipo: '',
    data: null,
    cliente: ''
  });
  const [clienti, setClienti] = useState([]);
  const [loading, setLoading] = useState(false);

  // Inizializza i filtri con i valori correnti
  useEffect(() => {
    setFiltri({
      stato: currentFiltri.stato || '',
      tipo: currentFiltri.tipo || '',
      data: currentFiltri.data ? new Date(currentFiltri.data) : null,
      cliente: currentFiltri.cliente || ''
    });
  }, [currentFiltri]);

  // Carica i clienti per il dropdown
  useEffect(() => {
    const loadClienti = async () => {
      setLoading(true);
      try {
        const result = await getClienti();
        setClienti(result.data);
      } catch (error) {
        console.error('Errore nel caricamento dei clienti:', error);
      } finally {
        setLoading(false);
      }
    };

    loadClienti();
  }, []);

  // Gestisce il cambio dei filtri
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFiltri({
      ...filtri,
      [name]: value
    });
  };

  // Gestisce il cambio della data
  const handleDateChange = (date) => {
    setFiltri({
      ...filtri,
      data: date
    });
  };

  // Gestisce l'applicazione dei filtri
  const handleApplyFilter = () => {
    // Prepara i filtri per l'invio escludendo quelli vuoti
    const filtersToApply = {};
    
    if (filtri.stato) filtersToApply.stato = filtri.stato;
    if (filtri.tipo) filtersToApply.tipo = filtri.tipo;
    if (filtri.data) filtersToApply.data = filtri.data.toISOString().split('T')[0];
    if (filtri.cliente) filtersToApply.cliente = filtri.cliente;
    
    onApplyFilter(filtersToApply);
  };

  return (
    <div>
      <h5 className="mb-3">Filtra Ordini</h5>
      <Row>
        <Col md={3}>
          <Form.Group className="mb-3">
            <Form.Label>Stato</Form.Label>
            <Form.Select
              name="stato"
              value={filtri.stato}
              onChange={handleChange}
            >
              <option value="">Tutti gli stati</option>
              <option value="nuovo">Nuovo</option>
              <option value="in preparazione">In Preparazione</option>
              <option value="pronto">Pronto</option>
              <option value="in consegna">In Consegna</option>
              <option value="consegnato">Consegnato</option>
              <option value="annullato">Annullato</option>
            </Form.Select>
          </Form.Group>
        </Col>
        <Col md={3}>
          <Form.Group className="mb-3">
            <Form.Label>Tipo</Form.Label>
            <Form.Select
              name="tipo"
              value={filtri.tipo}
              onChange={handleChange}
            >
              <option value="">Tutti i tipi</option>
              <option value="asporto">Asporto</option>
              <option value="consegna">Consegna</option>
              <option value="tavolo">Tavolo</option>
            </Form.Select>
          </Form.Group>
        </Col>
        <Col md={3}>
          <Form.Group className="mb-3">
            <Form.Label>Data</Form.Label>
            <DatePicker
              selected={filtri.data}
              onChange={handleDateChange}
              className="form-control"
              dateFormat="dd/MM/yyyy"
              locale={it}
              isClearable
              placeholderText="Seleziona data"
            />
          </Form.Group>
        </Col>
        <Col md={3}>
          <Form.Group className="mb-3">
            <Form.Label>Cliente</Form.Label>
            <Form.Select
              name="cliente"
              value={filtri.cliente}
              onChange={handleChange}
              disabled={loading}
            >
              <option value="">Tutti i clienti</option>
              {clienti.map((cliente) => (
                <option key={cliente._id} value={cliente._id}>
                  {`${cliente.nome} ${cliente.cognome}`}
                </option>
              ))}
            </Form.Select>
          </Form.Group>
        </Col>
      </Row>
      <div className="d-flex justify-content-end">
        <Button
          variant="outline-secondary"
          className="me-2"
          onClick={onResetFilter}
        >
          Reset
        </Button>
        <Button
          variant="primary"
          onClick={handleApplyFilter}
        >
          Applica Filtri
        </Button>
      </div>
    </div>
  );
};

export default OrdiniFilter;
