import React, { useState, useEffect } from 'react';
import { X, User, Phone, Home, Plus, Minus, RefreshCw, Search } from 'lucide-react';
import { getClienti } from '../../services/clientiService';
import ProductSelect from './ProductSelect';
import { Card } from 'react-bootstrap';

const OrdiniModal = ({ onClose, onSave }) => {
  // Stato per i dati del form
  const [formData, setFormData] = useState({
    cliente: null,
    telefono: '',
    indirizzo: '',
    tipo: 'consegna',
    prodotti: [],
    metodoPagamento: 'contanti',
    costoConsegna: 0,
    sconto: 0,
    note: ''
  });
  
  // Stati per il caricamento e la ricerca
  const [caricamento, setCaricamento] = useState(false);
  const [clienti, setClienti] = useState([]);
  const [ricercaCliente, setRicercaCliente] = useState('');
  const [clientiVisibili, setClientiVisibili] = useState(false);
  
  // Caricamento iniziale clienti
  useEffect(() => {
    const fetchClienti = async () => {
      try {
        setCaricamento(true);
        const data = await getClienti();
        setClienti(data);
      } catch (error) {
        console.error('Errore nel caricamento dei clienti:', error);
      } finally {
        setCaricamento(false);
      }
    };
    
    fetchClienti();
  }, []);
  
  // Gestione input form
  const handleInputChange = (e) => {
    const { name, value, type } = e.target;
    
    if (type === 'number') {
      setFormData(prev => ({
        ...prev,
        [name]: value === '' ? 0 : parseFloat(value)
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };
  
  // Selezione cliente
  const handleSelectCliente = (cliente) => {
    setFormData(prev => ({
      ...prev,
      cliente: cliente,
      telefono: cliente.telefono || '',
      indirizzo: cliente.indirizzo || ''
    }));
    setClientiVisibili(false);
    setRicercaCliente('');
  };
  
  // Filtro clienti in base alla ricerca
  const clientiFiltrati = clienti.filter(cliente => 
    cliente.nome.toLowerCase().includes(ricercaCliente.toLowerCase()) ||
    (cliente.telefono && cliente.telefono.includes(ricercaCliente))
  );
  
  // Aggiunta prodotto
  const handleAddProdotto = (prodotto) => {
    // Controlla se il prodotto è già nel carrello
    const esistente = formData.prodotti.findIndex(p => p._id === prodotto._id);
    
    if (esistente >= 0) {
      // Incrementa la quantità
      const nuoviProdotti = [...formData.prodotti];
      nuoviProdotti[esistente].quantita += 1;
      
      setFormData(prev => ({
        ...prev,
        prodotti: nuoviProdotti
      }));
    } else {
      // Aggiungi nuovo prodotto
      setFormData(prev => ({
        ...prev,
        prodotti: [...prev.prodotti, { ...prodotto, quantita: 1, note: '' }]
      }));
    }
  };
  
  // Rimozione prodotto
  const handleRemoveProdotto = (index) => {
    setFormData(prev => ({
      ...prev,
      prodotti: prev.prodotti.filter((_, i) => i !== index)
    }));
  };
  
  // Aggiorna quantità prodotto
  const handleUpdateQuantita = (index, valore) => {
    const nuoviProdotti = [...formData.prodotti];
    nuoviProdotti[index].quantita = Math.max(1, valore);
    
    setFormData(prev => ({
      ...prev,
      prodotti: nuoviProdotti
    }));
  };
  
  // Aggiorna note prodotto
  const handleUpdateNote = (index, note) => {
    const nuoviProdotti = [...formData.prodotti];
    nuoviProdotti[index].note = note;
    
    setFormData(prev => ({
      ...prev,
      prodotti: nuoviProdotti
    }));
  };
  
  // Calcolo del subtotale
  const calcolaSubtotale = () => {
    return formData.prodotti.reduce((total, p) => total + (p.prezzo * p.quantita), 0);
  };
  
  // Calcolo del totale
  const calcolaTotale = () => {
    const subtotale = calcolaSubtotale();
    return subtotale + formData.costoConsegna - formData.sconto;
  };
  
  // Formattazione prezzo
  const formatPrezzo = (prezzo) => {
    return `€ ${prezzo.toFixed(2)}`.replace('.', ',');
  };
  
  // Salvataggio ordine
  const handleSalvaOrdine = () => {
    // Validazione basilare
    if (formData.prodotti.length === 0) {
      alert('Aggiungi almeno un prodotto all\'ordine');
      return;
    }
    
    // Crea oggetto ordine da inviare
    const nuovoOrdine = {
      ...formData,
      stato: 'nuovo',
      dataOrdine: new Date(),
      subtotale: calcolaSubtotale(),
      totale: calcolaTotale()
    };
    
    // Invoca la callback per il salvataggio
    onSave(nuovoOrdine);
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-xl shadow-xl p-6 w-full max-w-4xl max-h-[90vh] overflow-y-auto">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-bold text-gray-800">Nuovo Ordine</h2>
          <button 
            className="p-1 rounded-full hover:bg-gray-200"
            onClick={onClose}
          >
            <X size={18} className="text-gray-600" />
          </button>
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {/* Colonna sinistra - Dati cliente e tipo ordine */}
          <div>
            <h3 className="font-medium text-gray-700 mb-3">Informazioni Cliente</h3>
            
            {/* Ricerca cliente */}
            <div className="mb-4 relative">
              <label className="block text-sm text-gray-600 mb-1">Cliente</label>
              <div className="relative">
                <input
                  type="text"
                  value={ricercaCliente}
                  onChange={(e) => {
                    setRicercaCliente(e.target.value);
                    setClientiVisibili(true);
                  }}
                  onFocus={() => setClientiVisibili(true)}
                  placeholder="Cerca cliente per nome o telefono..."
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-red-500"
                />
                <Search className="absolute left-3 top-2.5 text-gray-400" size={18} />
              </div>
              
              {/* Cliente selezionato */}
              {formData.cliente && (
                <div className="mt-2 bg-gray-50 p-3 rounded-lg flex items-start">
                  <div className="bg-red-100 rounded-full p-2 mr-3">
                    <User className="text-red-600" size={20} />
                  </div>
                  <div>
                    <h4 className="font-medium">{formData.cliente.nome}</h4>
                    <div className="flex items-center text-sm text-gray-600 mt-1">
                      <Phone size={14} className="mr-1" />
                      <span>{formData.cliente.telefono || 'N/D'}</span>
                    </div>
                    {formData.cliente.indirizzo && (
                      <div className="flex items-center text-sm text-gray-600 mt-1">
                        <Home size={14} className="mr-1" />
                        <span>{formData.cliente.indirizzo}</span>
                      </div>
                    )}
                  </div>
                  <button 
                    className="ml-auto p-1 rounded-full hover:bg-gray-200"
                    onClick={() => {
                      setFormData(prev => ({
                        ...prev,
                        cliente: null,
                        telefono: '',
                        indirizzo: ''
                      }));
                    }}
                  >
                    <X size={14} className="text-gray-600" />
                  </button>
                </div>
              )}
              
              {/* Dropdown clienti */}
              {clientiVisibili && ricercaCliente && (
                <div className="absolute z-10 mt-1 w-full bg-white rounded-md shadow-lg border border-gray-200 max-h-60 overflow-auto">
                  {caricamento ? (
                    <div className="p-3 text-center">
                      <RefreshCw size={20} className="animate-spin text-red-500 mx-auto mb-2" />
                      <p className="text-gray-500 text-sm">Caricamento clienti...</p>
                    </div>
                  ) : clientiFiltrati.length === 0 ? (
                    <div className="p-3 text-center text-gray-500">Nessun cliente trovato</div>
                  ) : (
                    clientiFiltrati.slice(0, 5).map(cliente => (
                      <div 
                        key={cliente._id}
                        className="px-3 py-2 hover:bg-gray-100 cursor-pointer"
                        onClick={() => handleSelectCliente(cliente)}
                      >
                        <div className="font-medium">{cliente.nome}</div>
                        <div className="text-xs text-gray-500">{cliente.telefono}</div>
                      </div>
                    ))
                  )}
                </div>
              )}
            </div>
            
            {/* Telefono */}
            <div className="mb-4">
              <label className="block text-sm text-gray-600 mb-1">Telefono*</label>
              <input
                type="text"
                name="telefono"
                value={formData.telefono}
                onChange={handleInputChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-red-500"
                placeholder="Es. 333-1234567"
                required
              />
            </div>
            
            {/* Indirizzo */}
            <div className="mb-4">
              <label className="block text-sm text-gray-600 mb-1">Indirizzo</label>
              <input
                type="text"
                name="indirizzo"
                value={formData.indirizzo}
                onChange={handleInputChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-red-500"
                placeholder="Es. Via Roma 123"
              />
            </div>
            
            {/* Tipo ordine */}
            <div className="mb-4">
              <label className="block text-sm text-gray-600 mb-1">Tipo ordine*</label>
              <div className="grid grid-cols-2 gap-2">
                <label className={`flex items-center justify-center p-3 rounded-lg cursor-pointer border ${formData.tipo === 'consegna' ? 'bg-red-50 border-red-500 text-red-700' : 'border-gray-300 hover:bg-gray-50'}`}>
                  <input
                    type="radio"
                    name="tipo"
                    value="consegna"
                    checked={formData.tipo === 'consegna'}
                    onChange={handleInputChange}
                    className="sr-only"
                  />
                  <Home className="mr-2" size={18} />
                  <span>Consegna a domicilio</span>
                </label>
                <label className={`flex items-center justify-center p-3 rounded-lg cursor-pointer border ${formData.tipo === 'asporto' ? 'bg-red-50 border-red-500 text-red-700' : 'border-gray-300 hover:bg-gray-50'}`}>
                  <input
                    type="radio"
                    name="tipo"
                    value="asporto"
                    checked={formData.tipo === 'asporto'}
                    onChange={handleInputChange}
                    className="sr-only"
                  />
                  <Home className="mr-2" size={18} />
                  <span>Asporto</span>
                </label>
              </div>
            </div>
            
            {/* Metodo di pagamento */}
            <div className="mb-4">
              <label className="block text-sm text-gray-600 mb-1">Metodo di pagamento</label>
              <select
                name="metodoPagamento"
                value={formData.metodoPagamento}
                onChange={handleInputChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-red-500"
              >
                <option value="contanti">Contanti alla consegna</option>
                <option value="carta">Carta di credito</option>
                <option value="paypal">PayPal</option>
                <option value="satispay">Satispay</option>
                <option value="pos">POS alla consegna</option>
              </select>
            </div>
            
            {/* Note */}
            <div className="mb-4">
              <label className="block text-sm text-gray-600 mb-1">Note</label>
              <textarea
                name="note"
                value={formData.note}
                onChange={handleInputChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-red-500 h-20 resize-none"
                placeholder="Note aggiuntive..."
              ></textarea>
            </div>
          </div>
          
          {/* Colonna destra - Prodotti e riepilogo */}
          <div>
            <h3 className="font-medium text-gray-700 mb-3">Prodotti</h3>
            
            {/* Selezione prodotti */}
            <ProductSelect onSelectProdotto={handleAddProdotto} />
            
            {/* Lista prodotti selezionati */}
            <div className="mt-4">
              <h4 className="text-sm font-medium text-gray-600 mb-2">Prodotti selezionati</h4>
              
              <div className="space-y-2 mb-4 max-h-64 overflow-y-auto">
                {formData.prodotti.length === 0 ? (
                  <p className="text-gray-500 text-sm p-2">Nessun prodotto aggiunto</p>
                ) : (
                  formData.prodotti.map((prodotto, index) => (
                    <div key={index} className="bg-gray-50 p-3 rounded-lg">
                      <div className="flex justify-between items-start">
                        <div>
                          <h5 className="font-medium">{prodotto.nome}</h5>
                          <p className="text-sm text-gray-600">{formatPrezzo(prodotto.prezzo)} cad.</p>
                        </div>
                        <div className="flex items-center">
                          <button 
                            className="p-1 rounded-full hover:bg-gray-200"
                            onClick={() => handleUpdateQuantita(index, prodotto.quantita - 1)}
                          >
                            <Minus size={14} className="text-gray-600" />
                          </button>
                          <span className="mx-2 min-w-[24px] text-center">
                            {prodotto.quantita}
                          </span>
                          <button 
                            className="p-1 rounded-full hover:bg-gray-200"
                            onClick={() => handleUpdateQuantita(index, prodotto.quantita + 1)}
                          >
                            <Plus size={14} className="text-gray-600" />
                          </button>
                          <button 
                            className="ml-3 p-1 rounded-full hover:bg-gray-200"
                            onClick={() => handleRemoveProdotto(index)}
                          >
                            <X size={14} className="text-gray-600" />
                          </button>
                        </div>
                      </div>
                      <div className="mt-2">
                        <input
                          type="text"
                          value={prodotto.note || ''}
                          onChange={(e) => handleUpdateNote(index, e.target.value)}
                          className="w-full px-2 py-1 text-sm border border-gray-300 rounded-lg focus:outline-none focus:ring-1 focus:ring-red-500"
                          placeholder="Note per questo prodotto..."
                        />
                      </div>
                    </div>
                  ))
                )}
              </div>
              
              {/* Totali */}
              <div className="p-4 bg-gray-50 rounded-lg shadow-sm">
                <div className="flex justify-between mb-2">
                  <span className="text-gray-600">Subtotale:</span>
                  <span className="font-medium">{formatPrezzo(calcolaSubtotale())}</span>
                </div>
                
                {formData.tipo === 'consegna' && (
                  <div className="flex items-center justify-between mb-2">
                    <label className="text-gray-600">Costo consegna:</label>
                    <div className="w-24">
                      <input
                        type="number"
                        name="costoConsegna"
                        value={formData.costoConsegna}
                        onChange={handleInputChange}
                        min="0"
                        step="0.5"
                        className="w-full px-2 py-1 text-right border border-gray-300 rounded-lg focus:outline-none focus:ring-1 focus:ring-red-500"
                      />
                    </div>
                  </div>
                )}
                
                <div className="flex items-center justify-between mb-2">
                  <label className="text-gray-600">Sconto:</label>
                  <div className="w-24">
                    <input
                      type="number"
                      name="sconto"
                      value={formData.sconto}
                      onChange={handleInputChange}
                      min="0"
                      step="0.5"
                      className="w-full px-2 py-1 text-right border border-gray-300 rounded-lg focus:outline-none focus:ring-1 focus:ring-red-500"
                    />
                  </div>
                </div>
                
                <div className="flex justify-between font-bold mt-3 pt-2 border-t border-gray-200">
                  <span>Totale:</span>
                  <span className="text-lg text-red-600">{formatPrezzo(calcolaTotale())}</span>
                </div>
              </div>
            </div>
          </div>
        </div>
        
        <div className="flex justify-end space-x-3 mt-6">
          <button 
            className="px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-100"
            onClick={onClose}
          >
            Annulla
          </button>
          <button 
            className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700"
            onClick={handleSalvaOrdine}
            disabled={!formData.telefono || formData.prodotti.length === 0}
          >
            {caricamento ? (
              <RefreshCw className="animate-spin mx-auto" size={20} />
            ) : (
              'Salva Ordine'
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

export default OrdiniModal;
