import React, { useState, useEffect } from 'react';
import { Card, Row, Col, Form, Button, Spinner } from 'react-bootstrap';
import { Bar, Pie } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
import DatePicker from 'react-datepicker';
import { it } from 'date-fns/locale';
import { format, subDays } from 'date-fns';
import { getStatisticheOrdini } from '../../services/ordiniService';

// Registra i componenti necessari di Chart.js
Chart.register(...registerables);

const StatisticheOrdini = () => {
  const [statistiche, setStatistiche] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filtri, setFiltri] = useState({
    dataInizio: subDays(new Date(), 30), // Ultimi 30 giorni di default
    dataFine: new Date()
  });

  useEffect(() => {
    fetchStatistiche();
  }, []);

  const fetchStatistiche = async () => {
    setLoading(true);
    try {
      const dataInizio = format(filtri.dataInizio, 'yyyy-MM-dd');
      const dataFine = format(filtri.dataFine, 'yyyy-MM-dd');
      
      const result = await getStatisticheOrdini({ dataInizio, dataFine });
      setStatistiche(result.data);
      setError(null);
    } catch (error) {
      console.error('Errore nel recupero delle statistiche:', error);
      setError('Impossibile caricare le statistiche. Riprova più tardi.');
    } finally {
      setLoading(false);
    }
  };

  const handleDateChange = (date, field) => {
    setFiltri({
      ...filtri,
      [field]: date
    });
  };

  const handleApplyFilter = () => {
    fetchStatistiche();
  };

  // Formatta prezzo in EUR
  const formatPrice = (price) => {
    return new Intl.NumberFormat('it-IT', { style: 'currency', currency: 'EUR' }).format(price || 0);
  };

  // Traduce lo stato in italiano
  const translateStato = (stato) => {
    const map = {
      'nuovo': 'Nuovo',
      'in preparazione': 'In Preparazione',
      'pronto': 'Pronto',
      'in consegna': 'In Consegna',
      'consegnato': 'Consegnato',
      'annullato': 'Annullato'
    };
    return map[stato] || stato;
  };

  // Dati per il grafico a torta degli stati
  const getPieChartData = () => {
    if (!statistiche || !statistiche.ordiniPerStato) return null;
    
    const backgroundColor = [
      '#4e73df', // Nuovo
      '#f6c23e', // In Preparazione
      '#36b9cc', // Pronto
      '#1cc88a', // In Consegna
      '#858796', // Consegnato
      '#e74a3b', // Annullato
    ];
    
    return {
      labels: statistiche.ordiniPerStato.map(item => translateStato(item._id)),
      datasets: [
        {
          data: statistiche.ordiniPerStato.map(item => item.count),
          backgroundColor,
          hoverBackgroundColor: backgroundColor,
          hoverBorderColor: 'rgba(234, 236, 244, 1)',
        },
      ],
    };
  };

  // Dati per il grafico a barre dei prodotti più venduti
  const getBarChartData = () => {
    if (!statistiche || !statistiche.prodottiPiuVenduti) return null;
    
    return {
      labels: statistiche.prodottiPiuVenduti.map(item => item.nome),
      datasets: [
        {
          label: 'Quantità Venduta',
          backgroundColor: '#4e73df',
          hoverBackgroundColor: '#2e59d9',
          data: statistiche.prodottiPiuVenduti.map(item => item.quantitaTotale),
        },
      ],
    };
  };

  if (loading) {
    return (
      <div className="text-center py-5">
        <Spinner animation="border" variant="primary" />
        <p className="mt-2">Caricamento statistiche in corso...</p>
      </div>
    );
  }

  if (error) {
    return (
      <Card className="mb-4">
        <Card.Body className="text-center py-5">
          <div className="text-danger mb-3">
            <i className="fas fa-exclamation-circle fa-3x"></i>
          </div>
          <h4>{error}</h4>
          <Button
            variant="primary"
            className="mt-3"
            onClick={fetchStatistiche}
          >
            Riprova
          </Button>
        </Card.Body>
      </Card>
    );
  }

  return (
    <div>
      <Card className="mb-4">
        <Card.Body>
          <h5 className="mb-3">Periodo di Analisi</h5>
          <Row>
            <Col md={4}>
              <Form.Group className="mb-3">
                <Form.Label>Data Inizio</Form.Label>
                <DatePicker
                  selected={filtri.dataInizio}
                  onChange={(date) => handleDateChange(date, 'dataInizio')}
                  selectsStart
                  startDate={filtri.dataInizio}
                  endDate={filtri.dataFine}
                  maxDate={filtri.dataFine}
                  className="form-control"
                  dateFormat="dd/MM/yyyy"
                  locale={it}
                />
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group className="mb-3">
                <Form.Label>Data Fine</Form.Label>
                <DatePicker
                  selected={filtri.dataFine}
                  onChange={(date) => handleDateChange(date, 'dataFine')}
                  selectsEnd
                  startDate={filtri.dataInizio}
                  endDate={filtri.dataFine}
                  minDate={filtri.dataInizio}
                  maxDate={new Date()}
                  className="form-control"
                  dateFormat="dd/MM/yyyy"
                  locale={it}
                />
              </Form.Group>
            </Col>
            <Col md={4} className="d-flex align-items-end">
              <Button
                variant="primary"
                className="mb-3"
                onClick={handleApplyFilter}
              >
                Applica Filtro
              </Button>
            </Col>
          </Row>
        </Card.Body>
      </Card>

      {statistiche && (
        <>
          <Row className="mb-4">
            <Col md={3}>
              <Card className="h-100 border-left-primary">
                <Card.Body>
                  <div className="row no-gutters align-items-center">
                    <div className="col mr-2">
                      <div className="text-xs font-weight-bold text-primary text-uppercase mb-1">
                        Totale Ordini
                      </div>
                      <div className="h5 mb-0 font-weight-bold text-gray-800">
                        {statistiche.statisticheGenerali.totaleOrdini || 0}
                      </div>
                    </div>
                    <div className="col-auto">
                      <i className="fas fa-calendar fa-2x text-gray-300"></i>
                    </div>
                  </div>
                </Card.Body>
              </Card>
            </Col>
            <Col md={3}>
              <Card className="h-100 border-left-success">
                <Card.Body>
                  <div className="row no-gutters align-items-center">
                    <div className="col mr-2">
                      <div className="text-xs font-weight-bold text-success text-uppercase mb-1">
                        Totale Vendite
                      </div>
                      <div className="h5 mb-0 font-weight-bold text-gray-800">
                        {formatPrice(statistiche.statisticheGenerali.totaleVendite)}
                      </div>
                    </div>
                    <div className="col-auto">
                      <i className="fas fa-euro-sign fa-2x text-gray-300"></i>
                    </div>
                  </div>
                </Card.Body>
              </Card>
            </Col>
            <Col md={3}>
              <Card className="h-100 border-left-info">
                <Card.Body>
                  <div className="row no-gutters align-items-center">
                    <div className="col mr-2">
                      <div className="text-xs font-weight-bold text-info text-uppercase mb-1">
                        Media Ordine
                      </div>
                      <div className="h5 mb-0 font-weight-bold text-gray-800">
                        {formatPrice(statistiche.statisticheGenerali.mediaOrdine)}
                      </div>
                    </div>
                    <div className="col-auto">
                      <i className="fas fa-clipboard-list fa-2x text-gray-300"></i>
                    </div>
                  </div>
                </Card.Body>
              </Card>
            </Col>
            <Col md={3}>
              <Card className="h-100 border-left-warning">
                <Card.Body>
                  <div className="row no-gutters align-items-center">
                    <div className="col mr-2">
                      <div className="text-xs font-weight-bold text-warning text-uppercase mb-1">
                        Consegne a Domicilio
                      </div>
                      <div className="h5 mb-0 font-weight-bold text-gray-800">
                        {statistiche.statisticheGenerali.totaleConsegne || 0}
                      </div>
                    </div>
                    <div className="col-auto">
                      <i className="fas fa-truck fa-2x text-gray-300"></i>
                    </div>
                  </div>
                </Card.Body>
              </Card>
            </Col>
          </Row>

          <Row>
            <Col md={6} className="mb-4">
              <Card className="h-100">
                <Card.Header>
                  <h6 className="m-0 font-weight-bold text-primary">Ordini per Stato</h6>
                </Card.Header>
                <Card.Body>
                  {getPieChartData() && (
                    <div style={{ height: '300px' }}>
                      <Pie
                        data={getPieChartData()}
                        options={{
                          maintainAspectRatio: false,
                          plugins: {
                            legend: {
                              position: 'bottom',
                            }
                          }
                        }}
                      />
                    </div>
                  )}
                </Card.Body>
              </Card>
            </Col>
            <Col md={6} className="mb-4">
              <Card className="h-100">
                <Card.Header>
                  <h6 className="m-0 font-weight-bold text-primary">Prodotti Più Venduti</h6>
                </Card.Header>
                <Card.Body>
                  {getBarChartData() && (
                    <div style={{ height: '300px' }}>
                      <Bar
                        data={getBarChartData()}
                        options={{
                          maintainAspectRatio: false,
                          plugins: {
                            legend: {
                              display: false
                            }
                          },
                          scales: {
                            y: {
                              beginAtZero: true,
                              ticks: {
                                precision: 0
                              }
                            }
                          }
                        }}
                      />
                    </div>
                  )}
                </Card.Body>
              </Card>
            </Col>
          </Row>
        </>
      )}
    </div>
  );
};

export default StatisticheOrdini;
